mapper.select:
select st.*,c.name as CategoryName from setmeal st left outer join category c on st.category_id = c.id


        and st.name like concat('%',#{name},'%')


        and st.category_id = #{categoryId}


        and st.status = #{status}


order by st.create_time desc

mapper.update:
update setmeal
description = #{description}, status = #{status}, category_id = #{categoryId}, image = #{image}, name = #{name}, price = #{price},
where id = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
